import React from "react";
import Chart from "../Chart/Chart";

function ExpenseChart(props) {
  const chartDataPoints = [
    { label: "Jan", value: 0 },
    { label: "Feb", value: 0 },
    { label: "Mar", value: 0 },
    { label: "May", value: 0 },
    { label: "May", value: 0 },
    { label: "Jun", value: 0 },
    { label: "Jul", value: 0 },
    { label: "Aug", value: 0 },
    { label: "Sep", value: 0 },
    { label: "Oct", value: 0 },
    { label: "Nov", value: 0 },
    { label: "Dec", value: 0 },
  ];
  for (expense of props.expenses) {
    const expenseMonth = props.expense.getMonth();
    chartDataPoints[expenseMonth].value = +expense.amount;
  }
  return (
    <div>
      <Chart dataPoint={chartDataPoints} />
    </div>
  );
}

export default ExpenseChart;
